select

from
tb_natmeha_hotspot h
where
h.DATA_TYPE = #{dataType}


    and CONCAT(h.HOTSPOT_TITLE,h.HOTSPOT_SOURCE,h.HOTSPOT_AUTHOR) like #{pattern}

select
from tb_natmeha_hotspot h where h.DATA_TYPE = '3'
and CONCAT(h.HOTSPOT_TITLE,h.HOTSPOT_SOURCE,h.HOTSPOT_AUTHOR) like #{pattern}

select
from tb_natmeha_hotspot h where h.DATA_TYPE = #{dataType} and h.DATA_STATUS = '1'
and CONCAT(h.HOTSPOT_TITLE,h.HOTSPOT_SOURCE,h.HOTSPOT_AUTHOR) like #{pattern}

select
from tb_natmeha_hotspot h where h.DATA_TYPE = #{dataType} and h.DATA_STATUS in ('9','3','4','5','6','7','8','11')
and CONCAT(h.HOTSPOT_TITLE,h.HOTSPOT_SOURCE,h.HOTSPOT_AUTHOR) like #{pattern}

select
from tb_natmeha_hotspot h where h.DATA_TYPE = #{dataType} and h.DATA_STATUS = '3'
and CONCAT(h.HOTSPOT_TITLE,h.HOTSPOT_SOURCE,h.HOTSPOT_AUTHOR) like #{pattern}

select
from tb_natmeha_hotspot h where h.DATA_TYPE = #{dataType} and h.DATA_STATUS in ('10','5','6','7','8','11')
and CONCAT(h.HOTSPOT_TITLE,h.HOTSPOT_SOURCE,h.HOTSPOT_AUTHOR) like #{pattern}

select
from tb_natmeha_hotspot h where h.DATA_TYPE = #{dataType} and h.DATA_STATUS = '5'
and CONCAT(h.HOTSPOT_TITLE,h.HOTSPOT_SOURCE,h.HOTSPOT_AUTHOR) like #{pattern}

